import * as cgeo from 'cgeo';

export type This = Geometry & cgeo.Geometry;

@cgeo.mixin(cgeo.Geometry as any as { new(): cgeo.Geometry })
export class Geometry {

	getSimpleArea() { return(0); }

	/** Helper function for area of arbitrary polygon rings. */

	getFilledArea() { return(0); }

	getSimpleCentroid(): cgeo.Point {
		const result = new cgeo.Point(0, 0);

		result.weight = 0;
		this.updateSimpleCentroid(result);

		// For empty geometries, this sets centroid coordinates to NaN.
		result.x /= result.weight!;
		result.y /= result.weight!;

		return(result);
	}

	/** Helper function for centroids of arbitrary curves and surfaces.
	  * Updates input point weight and coordinates.
	  * Centroid is at coordinates divided by weight. */
	updateSimpleCentroid(result: cgeo.Point) {}

	/** Helper function for centroids of arbitrary polygon rings.
	  * Updates input point weight and coordinates.
	  * Centroid is at coordinates divided by weight. */
	updateFilledCentroid(result: cgeo.Point, sign: number) {}

}
