import * as cgeo from 'cgeo';

@cgeo.mixin()
export class Point extends cgeo.Point {

	updateSimpleCentroid(result: cgeo.Point) {
		result.x += this.x;
		result.y += this.y;
		++result.weight!;
	}

}
