import * as cgeo from 'cgeo';
import 'cgeo-wkt';
import '..';

const polygon1 = new cgeo.Polygon([[
	{ x: 0, y: 0 },
	{ x: 2, y: 0 },
	{ x: 2, y: 1 },
	{ x: 1, y: 1 },
	{ x: 1, y: 2 },
	{ x: 0, y: 2 },
	{ x: 0, y: 0 }
]]);

const polygon2 = new cgeo.Polygon([
	[
		{ x: 0, y: 0 },
		{ x: 2, y: 0 },
		{ x: 2, y: 2 },
		{ x: 0, y: 2 },
		{ x: 0, y: 0 }
	], [
		{ x: 1, y: 1 },
		{ x: 2, y: 1 },
		{ x: 2, y: 2 },
		{ x: 1, y: 2 },
		{ x: 1, y: 1 }
	],
]);

const polygon3 = new cgeo.Polygon([
	[
		{ x: 0, y: 0 },
		{ x: 2, y: 0 },
		{ x: 2, y: 2 },
		{ x: 0, y: 2 },
		{ x: 0, y: 0 }
	], [
		{ x: 1, y: 1 },
		{ x: 1, y: 2 },
		{ x: 2, y: 2 },
		{ x: 2, y: 1 },
		{ x: 1, y: 1 }
	],
]);

console.log('ST_AsText(ST_Centroid(ST_GeomFromText(\'' + polygon1.toWKT() + '\')))');
console.log(polygon1.getSimpleArea());
console.log(polygon2.getSimpleArea());
console.log(polygon3.getSimpleArea());
console.log(polygon1.getSimpleCentroid());
console.log(polygon2.getSimpleCentroid());
console.log(polygon3.getSimpleCentroid());

console.log(
	new cgeo.GeometryCollection([
		new cgeo.Polygon([[{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 1, y: 1 }, { x: 0, y: 1 }, { x: 0, y: 0 }]]),
		new cgeo.Point(10, 10)
	] as cgeo.Geometry[]).getSimpleArea()
);

console.log(
	new cgeo.GeometryCollection([
		new cgeo.LineString([{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 2, y: 0 }, { x: 2, y: 2 }]),
		new cgeo.Point(10, 10)
	] as cgeo.Geometry[]).getSimpleCentroid().toWKT()
);

console.log(
	new cgeo.MultiPoint([
		{ x: 0, y: 0 }, { x: 1, y: 1 }, { x: 2, y: 2 }
	]).getSimpleCentroid().toWKT()
);
