import * as cgeo from 'cgeo';
import { State } from './Geometry';

@cgeo.mixin()
export class CircularString extends cgeo.CircularString {

	writeGML(state: State) {
		return(cgeo.LineString.prototype.writeGML.call(this, state, state.gml.ArcString));
	}

}
