import * as cgeo from 'cgeo';
import { State } from './Geometry';

@cgeo.mixin()
export class CompoundCurve extends cgeo.CompoundCurve {

	writeGML(state: State) {
		return(cgeo.MultiCurve.prototype.writeGML.call(this, state));
	}

}
