import * as cgeo from 'cgeo';
import { State } from './Geometry';

@cgeo.mixin()
export class CurvePolygon extends cgeo.CurvePolygon {

	writeGML(state: State) {
		return(cgeo.Polygon.prototype.writeGML.call(this, state));
	}

}
