import * as cxml from 'cxml';
import * as cgeo from 'cgeo';
import { ConfigGML } from './ConfigGML';

export class GeometryToken extends cxml.Token {

	constructor(public geometry: cgeo.Geometry) { super(); }

	serialize(indent: string, config: cxml.ParserConfig, data: any): string | cxml.TokenBuffer {
		return(this.geometry.toGML(data.gmlConfig as ConfigGML));
	}

}
