import * as cgeo from 'cgeo';
import { WriteState } from './Geometry';
import { Parser } from './Parser';

const empty: number[] = [];

@cgeo.mixin()
export class Point extends cgeo.Point {

	writeGML(state: WriteState) {
		const { gml, buffer } = state;
		let pt: string;

		if(state.flipXY) {
			pt = this.y + ' ' + this.x;
		} else {
			pt = this.x + ' ' + this.y;
		}

		buffer.push(
			gml.Point,
			gml.Point.emitted,
			gml.pos,
			gml.pos.emitted,
			pt,
			gml.pos.close,
			gml.Point.close
		);
	}

	static initGML(parser: Parser<any>) {
		const x: number[] = [];
		const y: number[] = [];

		parser.coordList = { x, y };
		parser.target = [ x, y ];

		if(parser.dimension > 2) {
			parser.target.push(parser.coordList.z = []);
		}

		if(parser.dimension > 3) {
			parser.target.push(parser.coordList.m = []);
		}
	}

	static finishGML(parser: Parser<any>): cgeo.Point | null {
		const coordList = parser.coordList;

		if(!coordList) return(null);

		parser.coordList = void 0;

		return(new cgeo.Point(
			(coordList.x || empty)[0],
			(coordList.y || empty)[0],
			(coordList.z || empty)[0],
			(coordList.m || empty)[0]
		));
	}

}
