import * as cgeo from 'cgeo';
import { State } from './Geometry';

@cgeo.mixin()
export class Point extends cgeo.Point {

	writeGML(state: State) {
		const { gml, buffer } = state;
		let pt: string;

		if(state.flipXY) {
			pt = this.y + ' ' + this.x;
		} else {
			pt = this.x + ' ' + this.y;
		}

		buffer.push(
			gml.Point,
			gml.Point.emitted,
			gml.pos,
			gml.pos.emitted,
			pt,
			gml.pos.close,
			gml.Point.close
		);
	}

}
