import * as cgeo from 'cgeo';
import { State } from './Geometry';

@cgeo.mixin()
export class PolyhedralSurface extends cgeo.PolyhedralSurface {

	measureWKB(state: State) {
		return(cgeo.MultiPolygon.prototype.measureWKB.call(this, state));
	}

	writeWKB(state: State) {
		return(cgeo.MultiPolygon.prototype.writeWKB.call(this, state));
	}

	readWKB(state: State) {
		return(cgeo.MultiPolygon.prototype.readWKB.call(this, state));
	}

}
