import { GeometryKind, registerType } from './Geometry';
import { Surface } from './Surface';
import { Polygon } from './Polygon';
import { MultiPolygon, MultiPolygonSpec, initPolygons } from './MultiPolygon';

export class PolyhedralSurface extends Surface implements MultiPolygon {

	constructor(childList: MultiPolygonSpec = []) {
		super();

		initPolygons(this, childList);
	}

	addChild(child: Polygon) { this.childList.push(child); }

	childList: Polygon[] = [];

}

registerType(PolyhedralSurface, GeometryKind.polyhedralSurface, 1);
