import { GeometryKind, registerType } from './Geometry';
import { Surface } from './Surface';
import { Polygon } from './Polygon';
import { MultiSurface } from './MultiSurface';

export class PolyhedralSurface extends Surface implements MultiSurface {

	constructor(childList?: Surface[]) {
		super();

		for(let child of (childList || [])) {
			this.addChild(child);
		}
	}

	addChild(child: Surface) { this.childList.push(child); }

	childList: Surface[] = [];

}

registerType(PolyhedralSurface, GeometryKind.polyhedralSurface, 1);
