export function jsx<Element, Attribute>(e: () => Element, a: () => Attribute) {
	return({
		xmlns: 'http://www.opengis.net/gml',

		Point: e(),
		LineString: e(),
		Polygon: e(),

		Curve: e(),
		Arc: e(),
		ArcString: e(),
		CompositeCurve: e(),

		MultiGeometry: e(),
		MultiPoint: e(),
		MultiLineString: e(),
		MultiPolygon: e(),
		MultiCurve: e(),
		MultiSurface: e(),

		PolyhedralSurface: e(),
		polygonPatches: e(),
		PolygonPatch: e(),

		featureMembers: e(),
		featureMember: e(),
		geometryMembers: e(),
		geometryMember: e(),
		pointMembers: e(),
		pointMember: e(),
		lineStringMembers: e(),
		lineStringMember: e(),
		polygonMembers: e(),
		polygonMember: e(),
		curveMembers: e(),
		curveMember: e(),
		surfaceMembers: e(),
		surfaceMember: e(),

		exterior: e(),
		interior: e(),
		LinearRing: e(),
		Ring: e(),

		segments: e(),
		LineStringSegment: e(),

		coordinates: e(),
		pos: e(),
		posList: e(),

		id: a(),

		srsDimension: a(),
		srsName: a()
	});
}
