export function jsx<Type>(t: () => Type) {
	return({
		xmlns: 'http://www.opengis.net/gml',

		Point: t(),
		LineString: t(),
		Polygon: t(),

		Curve: t(),
		Arc: t(),
		ArcString: t(),
		CompositeCurve: t(),

		MultiGeometry: t(),
		MultiPoint: t(),
		MultiLineString: t(),
		MultiPolygon: t(),
		MultiCurve: t(),
		MultiSurface: t(),

		PolyhedralSurface: t(),
		polygonPatches: t(),
		PolygonPatch: t(),

		featureMembers: t(),
		featureMember: t(),
		geometryMembers: t(),
		geometryMember: t(),
		pointMembers: t(),
		pointMember: t(),
		lineStringMembers: t(),
		lineStringMember: t(),
		polygonMembers: t(),
		polygonMember: t(),
		curveMembers: t(),
		curveMember: t(),
		surfaceMembers: t(),
		surfaceMember: t(),

		exterior: t(),
		interior: t(),
		LinearRing: t(),
		Ring: t(),

		segments: t(),
		LineStringSegment: t(),

		coordinates: t(),
		pos: t(),
		posList: t()
	});
}
