export function jsx<Type>(t: () => Type) {
	return({
		xmlns: 'http://www.opengis.net/wfs',

		WFS_Capabilities: t(),

		FeatureTypeList: t(),
		FeatureType: t(),

		Name: t(),
		Title: t(),

		GetFeature: t(),

		Query: t(),
		Filter: t(),
		BBOX: t(),
		Intersects: t(),
		Envelope: t(),
		lowerCorner: t(),
		upperCorner: t(),

		FeatureCollection: t()
	});
}
