/** Calculate sign of the 2D cross product AKA perp dot product AKA 2x2 matrix
  * determinant. */

export function detSign(a1: number, b1: number, b2: number, a2: number) {
	const a = a1 * a2;
	const b = b1 * b2;

	// Return Math.sign(a - b)
	return(+(a > b) - +(a < b));
}

/** Comparison function for sorting lines by angle.
  * Numerically very robust, only comparing two products of inputs.
  * @return Some positive or negative number or zero, depending on the sorting order. */

export function angleDeltaFrom(x1: number, y1: number, x2: number, y2: number) {
	const d1 = x1 > y1 ? 1 : 0;
	const d2 = x2 > y2 ? 1 : 0;
	const a1 = x1 > -y1 ? 1 : 0;
	const a2 = x2 > -y2 ? 1 : 0;

	// Quotients of both angles.
	const qa = d1 * 2 + a1;
	const qb = d2 * 2 + a2;

	if(qa != qb) return((0x6c >> qa * 2 & 6) - (0x6c >> qb * 2 & 6));

	return(d1 ^ a1 ?
		detSign(-x1, -y1, x2, y2) :
		detSign( x2,  y2, x1, y1)
	);
}
