export { cuberoot, testQuad, solveQuad, testCubic, solveCubic } from './polynomial';

export function detSign(a1: number, b1: number, b2: number, a2: number) {
	const a = a1 * a2;
	const b = b1 * b2;

	return(+(b < a) - +(a < b));
}
