import { GeoTree, GeoTile } from '..';

for(let i = 0; i < 10000; ++i) {
	const tree = new GeoTree(0, 0, 8, 8);

	let s = Math.random() * 8;
	let w = Math.random() * 8;
	let n = Math.random() * 8;
	let e = Math.random() * 8;

	if(s > n) { let t = s; s = n; n = t; }
	if(w > e) { let t = w; w = e; e = t; }

	// console.log(tree.splitBy(1.9, 1.9, 3.1, 3.1).length);
	console.log(tree.splitBy(s, w, n, e).map((tile: GeoTile) => tile.path));
	// console.log(tree.splitBy(s, w, n, e).length);
}

